const recipes = [
    {
        dish: "Spinach and Artichoke Dip",
        ing: "12oz Cream Cheese, 1/2 cup ricotta cheese, 2 cans artichoke hearts, 10 oz spinach, 1 cup monterey jack cheese, 1/2 cup parmesean, 1/2 cup onion poweder, 1/2 tsp garlic salt, 1 tsp black pepper",
        dir: "Mix all inredients in large bowl. cook in crock pot until warm.",
        img: "data:image/jpeg;base64,",
        comment: "Great for parties"
    },
    {
        dish: "Chocolate Moose Dessert",
        ing: "2 instant pudding, cool whip, 2 cream cheese, 1 cup powdered sugar, 1 stick softened butter, 1 cup flower, 2 cups milk, 1/2 cup chopped nuts",
        dir: "mix flour nuts and butter press onto the bottom of a 9x13 pan bake at 350 for 15 min. Mix 2 chocolate pudding with 2 cups of milk, then add 1 cup of coolwhip. Mix cream cheese and powdered sugar layer this on crust, then layer pudding mixture. Top with remaining coolwhip",
        img: "data:image/jpeg;base64,",
        comment: "great without nuts too!"
    },



]
const favorites = []


const getFavorites = (req, res) => {
    res.json(favorites)
}

const getRecipes = (req, res) => {
    res.json(recipes)
}
const addRecipe = (req, res) => {
    const { dish, ing, dir, comment, img } = req.body;
    if (!dish || !ing || !dir || !comment || !img) {
        return res.status(404).json("All Fields Required")
    }
    recipes.unshift({ dish, ing, dir, comment, img })
    res.json(recipes)
}
const editComment = (req, res) => {
    const { comment } = req.body;
    const updateDish = req.params.dish;
    const recipeIndex = recipes.findIndex(recipe => recipe.dish == updateDish);
    let recipe = recipes[recipeIndex]

    recipes[recipeIndex] = {
        dish: recipe.dish,
        ing: recipe.ing,
        dir: recipe.dir,
        comment: comment || recipe.comment,
        img: recipe.img
    }

    res.json(recipes)
}


const removeFavorite = (req, res) => {

    const addDish = req.params.dish
    const recipeIndex = favorites.findIndex(recipe => recipe.dish == addDish)
    let recipe = favorites[recipeIndex]

    favorites.splice(recipe, 1)



    res.json(favorites)
}

const addFavorite = (req, res) => {

    const removeDish = req.params.dish
    const recipeIndex = recipes.findIndex(recipe => recipe.dish == removeDish)
    let recipe = recipes[recipeIndex]

    favorites.unshift(recipe)



    res.json(favorites)
}

module.exports = {
    getRecipes,
    addRecipe,
    editComment,
    addFavorite,
    getFavorites,
    removeFavorite
}